import { Component } from '@angular/core';
import { Router } from 'express';
import { CookieService } from 'ngx-cookie-service';

export interface AppUser {
  fullName: string
}

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
